// Fonction pour soumettre une question
function submitQuestion() {
    const userInput = document.getElementById("userInput").value.toLowerCase().trim();
    const responsesDiv = document.getElementById("responses");

    // Effacer le champ de saisie
    document.getElementById("userInput").value = '';

    // Chercher une r√©ponse dans la FAQ en utilisant les mots-cl√©s
    const match = faqList.find(faq => userInput.includes(faq.question));

    let responseMessage;
    if (match) {
        responseMessage = match.response;
    } else {
        // Ajouter √† la liste des questions non reconnues
        unknownQuestions.push(userInput);

        // Sugg√©rer des questions similaires
        const suggestions = faqList
            .filter(faq => userInput.split(" ").some(word => faq.question.includes(word)))
            .map(faq => faq.question)
            .slice(0, 4); // Limiter le nombre de suggestions

        responseMessage = "D√©sol√©, je ne comprends pas la question. Peut-√™tre avez-vous voulu dire : <br>" +
            `<div class="suggestions">${suggestions.join(", ")}</div>`;
    }

    // Ajouter les messages √† l'interface utilisateur
    responsesDiv.innerHTML += `<div><strong>Vous:</strong> ${userInput}</div>`;
    responsesDiv.innerHTML += `<div class="response"><span class="title">HBA-AI:</span> ${responseMessage}</div>`;

    // Faire d√©filer la bo√Æte de chat vers le bas
    const chatbox = document.getElementById("chatbox");
    chatbox.scrollTop = chatbox.scrollHeight;
}





